import meter1 from "../assets/img/meter1.svg";  // Image pour les pourcentages élevés
import meter2 from "../assets/img/meter2.svg";  // Image pour les pourcentages moyens
import meter3 from "../assets/img/meter3.svg";  // Image pour les pourcentages bas

import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import arrow1 from "../assets/img/arrow1.svg";
import arrow2 from "../assets/img/arrow2.svg";
import colorSharp from "../assets/img/color-sharp.png";

export const Skills = () => {
  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 5
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };

  const skillsData = [
    { skill: "Python", percentage: 50, image: meter3 },
    { skill: "Java", percentage: 90, image: meter1 },
    { skill: "JavaScript", percentage: 80, image: meter1 },
    { skill: "HTML", percentage: 90, image: meter1 },
    { skill: "CSS", percentage: 70, image: meter2 },
    { skill: "React", percentage: 75, image: meter2 },
    { skill: "MongoDB", percentage: 60, image: meter2 },
    { skill: "Git", percentage: 70, image: meter2 },
    { skill: "GitHub", percentage: 80, image: meter1 },
    { skill: "Linux", percentage: 40, image: meter3 },
    { skill: "PHP", percentage: 50, image: meter3 },
    { skill: "MySQL", percentage: 70, image: meter2 },
    { skill: "Montage Vidéos", percentage: 60, image: meter2 },
    { skill: "Microsoft PowerPoint", percentage: 80, image: meter1 },
    { skill: "Adobe Photoshop", percentage: 50, image: meter3 }
  ];

  return (
    <section className="skill" id="skills">
        <div className="container">
            <div className="row">
                <div className="col-12">
                    <div className="skill-bx wow zoomIn">
                        <h2>Skills</h2>
                        
                        <Carousel responsive={responsive} infinite={true} className="owl-carousel owl-theme skill-slider">
                            {skillsData.map((skill, index) => (
                              <div className="item" key={index}>
                                  <img src={skill.image} alt={skill.skill} />
                                  <h5>{skill.skill} - {skill.percentage}%</h5>
                              </div>
                            ))}
                        </Carousel>
                    </div>
                </div>
            </div>
        </div>
        <img className="background-image-left" src={colorSharp} alt="Image" />
    </section>
  );
}
