import React from 'react';
import { Container, Row, Col, Button } from "react-bootstrap";
import { useLocation, Link } from "react-router-dom"; // Assuming you're using react-router-dom

export const ProjectsScreen = () => {
  const location = useLocation();
  const { project } = location.state; // project passed via router

  return (
    <section className="project-screen">
      <Container>
        <Row>
          <Col>
            <h2>{project.title}</h2>
            <img src={project.imgUrl} alt={project.title} style={{ width: "100%", height: "auto" }} />
            <p>{project.description}</p>
            {/* Button to GitHub */}
            <Button variant="primary" href={project.githubUrl} target="_blank" rel="noopener noreferrer">
              Code Source
            </Button>
            {/* Go Back Link */}
            <Link to="/projects" className="btn btn-secondary mt-3">Go Back</Link>
          </Col>
        </Row>
      </Container>
    </section>
  );
};
