import React from 'react';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import colorSharp from "../assets/img/color-sharp.png";

// Direct URLs to the images
const meterImages = {
  40: "https://img.vecteezy.com/png/25740569.png", // 40%
  50: "https://img.vecteezy.com/png/25740582.png", // 50%
  60: "https://img.vecteezy.com/png/25740582.png", // 60% (No direct URL provided, using 50% image as placeholder)
  70: "https://img.vecteezy.com/png/25740567.png", // 70%
  75: "https://img.vecteezy.com/png/25740590.png", // 75%
  80: "https://img.vecteezy.com/png/25740563.png", // 80%
  90: "https://img.vecteezy.com/png/25740589.png", // 90%
  95: "https://img.vecteezy.com/png/25740593.png"  // 95%
};

export const Skills = () => {
  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 5
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };

  const skillsData = [
    { skill: "Python", percentage: 50, image: meterImages[50] },
    { skill: "Java", percentage: 90, image: meterImages[90] },
    { skill: "JavaScript", percentage: 80, image: meterImages[80] },
    { skill: "HTML", percentage: 90, image: meterImages[90] },
    { skill: "CSS", percentage: 70, image: meterImages[70] },
    { skill: "React", percentage: 75, image: meterImages[75] },
    { skill: "MongoDB", percentage: 60, image: meterImages[60] },
    { skill: "Git", percentage: 70, image: meterImages[70] },
    { skill: "GitHub", percentage: 80, image: meterImages[80] },
    { skill: "Linux", percentage: 40, image: meterImages[40] },
    { skill: "PHP", percentage: 50, image: meterImages[50] },
    { skill: "MySQL", percentage: 70, image: meterImages[70] },
    { skill: "Montage Vidéos", percentage: 60, image: meterImages[60] },
    { skill: "Microsoft PowerPoint", percentage: 80, image: meterImages[80] },
    { skill: "Adobe Photoshop", percentage: 50, image: meterImages[50] }
  ];

  return (
    <section className="skill" id="skills">
        <div className="container">
            <div className="row">
                <div className="col-12">
                    <div className="skill-bx wow zoomIn">
                        <h2>Skills</h2>
                        
                        <Carousel responsive={responsive} infinite={true} className="owl-carousel owl-theme skill-slider">
                            {skillsData.map((skill, index) => (
                              <div className="item" key={index}>
                                  <img src={skill.image} alt={skill.skill} />
                                  <h5>{skill.skill} - {skill.percentage}%</h5>
                              </div>
                            ))}
                        </Carousel>
                    </div>
                </div>
            </div>
        </div>
        <img className="background-image-left" src={colorSharp} alt="Image" />
    </section>
  );
}
