import { useState, useEffect } from "react";
import { Col, Row, Alert } from "react-bootstrap";

export const Newsletter = () => {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState({ success: null, message: '' });
  const [buttonText, setButtonText] = useState("Send");

  useEffect(() => {
    if (status.success) {
      clearFields();
    }
  }, [status]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setButtonText("Sending...");
    try {
      let response = await fetch("http://localhost:5000/contact", {
        method: "POST",
        headers: {
          "Content-Type": "application/json;charset=utf-8",
        },
        body: JSON.stringify({
          firstName: "",  // Replace with form data if needed
          lastName: "",   // Replace with form data if needed
          email: email,
          message: "",    // Replace with form data if needed
          phone: ""       // Replace with form data if needed
        }),
      });
      let result = await response.json();
      if (response.ok) {
        setStatus({ success: true, message: 'Message sent successfully' });
      } else {
        setStatus({ success: false, message: result.message || 'Something went wrong, please try again later.' });
      }
    } catch (error) {
      console.error('Error:', error);
      setStatus({ success: false, message: 'Network error, please try again later.' });
    } finally {
      setButtonText("Send");
      clearFields();
    }
  };

  const clearFields = () => {
    setEmail('');
  };

  return (
    <Col lg={12}>
      <div className="newsletter-bx wow slideInUp">
        <Row>
          <Col lg={12} md={6} xl={5}>
            <h3>Subscribe to our Newsletter<br />& Never miss latest updates</h3>
            {status.success === null && <Alert>Sending...</Alert>}
            {status.success === false && <Alert variant="danger">{status.message}</Alert>}
            {status.success === true && <Alert variant="success">{status.message}</Alert>}
          </Col>
          <Col md={6} xl={7}>
            <form onSubmit={handleSubmit}>
              <div className="new-email-bx">
                <input 
                  value={email} 
                  type="email" 
                  onChange={(e) => setEmail(e.target.value)} 
                  placeholder="Email Address" 
                />
                <button type="submit">{buttonText}</button>
              </div>
            </form>
          </Col>
        </Row>
      </div>
    </Col>
  );
};
